// Gestion des connexions avec les rôles, s'applique comm suit : router.post("/product/add",rle.isAdmin, function (req, resp)...
// Avec en début des fichiers, var rle = require('rolechecker');
var isAnonymous = function(req,resp,next){
	if(typeof req.user!='undefined') return next();}

var isAdmin = function(req,resp,next){
	if(typeof req.user!='undefined')
	{if(req.user.role=='ROLE_ADMIN')
		//next : passe à la function suivante
		{return next()}}
	resp.redirect('/login.html');
	};
	
	exports.isAdmin=isAdmin;
	exports.isAnonymous=isAnonymous;